import React, { Children } from "react"
import { Drawer } from "antd"

export interface DrawerProps {
  title: string
  placement: "top" | "right" | "bottom" | "left"
  visible: boolean
  isCollapsed: boolean
  children: any
}

const Index = ({ title, placement, visible, isCollapsed, children }: DrawerProps) => {
  return (
    <>
      <Drawer
        title={title}
        placement={placement}
        closable={false}
        // onClose={this.onClose}
        visible={visible}
        key={placement}
        mask={false}
        zIndex={1}
        className={isCollapsed ? "" : "isCollapsed"}
        headerStyle={{ height: 10, 'padding-left': 10, 'padding-right': 10 }}
        bodyStyle={{ 'padding-left': 10, 'padding-right': 10, 'padding-top': 10 }}
      >
        {children}
      </Drawer>
    </>
  )
}

export default Index
