import { IResponseCurrentUser } from "types/auth"

export interface IAuthState {
  auth: null | IResponseCurrentUser
  loading: boolean
  error: string | null
  authorized: boolean
  authorizing: boolean
}

export enum AuthActions {
  LOGIN = "[auth] login",
  LOGIN_SUCCESS = "[auth] login success",
  LOGIN_FAILED = "[auth] login failed",
  LOGOUT = "[auth] logout",
  LOGOUT_SUCCESS = "[auth] logout success",
  LOGOUT_FAILED = "[auth] logout failed",
  GET_CURRENT_USER = "[auth] get current user",
}

export interface ILoginAction {
  type: AuthActions.LOGIN
  payload: Record<"email" | "password", string>
}

export interface ILoginSuccessAction {
  type: AuthActions.LOGIN_SUCCESS
}

export interface ILoginFailedAction {
  type: AuthActions.LOGIN_FAILED
  payload: { error: string }
}

export interface ILogoutAction {
  type: AuthActions.LOGOUT
}

export interface ILogoutSuccessAction {
  type: AuthActions.LOGOUT_SUCCESS
}

export interface ILogoutFailedAction {
  type: AuthActions.LOGOUT_FAILED
  payload: { error: string }
}

export interface IGetCurrentUserAction {
  type: AuthActions.GET_CURRENT_USER
}
export type AuthActionTypes =
  | ILoginAction
  | ILoginSuccessAction
  | ILoginFailedAction
  | ILogoutAction
  | ILogoutSuccessAction
  | ILogoutFailedAction
  | IGetCurrentUserAction
