import { notification } from "antd"
import { all, call, put, takeLatest } from "redux-saga/effects"
import authServices from "services/auth/api"
import { get } from "lodash"
import { BaseFunctions, loginFailed, loginSuccess, logoutSuccess, logoutFailed } from "./actions"
import { AuthActions, ILoginAction } from "./types"

export function* login({ payload }: ILoginAction) {
  yield put(BaseFunctions.setState({ loading: true }))
  try {
    yield call(authServices.login, payload)
    yield put(loginSuccess())
  } catch (e) {
    notification.error({ message: "Error", description: get(e, "data.message") })
    yield put(loginFailed({ error: get(e, "data.message") }))
  }
}

export function* logout() {
  yield put(BaseFunctions.setState({ loading: true }))
  try {
    yield call(authServices.logout)
    yield put(logoutSuccess())
  } catch (e) {
    notification.error({ message: "Error", description: get(e, "data.message") })
    yield put(logoutFailed({ error: get(e, "data.message") }))
  }
}

export function* getCurrentUser() {
  try {
    yield put(BaseFunctions.setState({ authorizing: true }))
    const data = yield call(authServices.getCurrentUser)
    yield put(BaseFunctions.setState({ authorizing: false, authorized: true }))
  } catch (e) {
    yield put(loginFailed({ error: get(e, "data.message") }))
    yield put(BaseFunctions.setState({ authorizing: false, authorized: false }))
  }
}

export default function* rootSaga() {
  yield all([
    takeLatest(AuthActions.LOGIN, login),
    takeLatest(AuthActions.LOGOUT, logout),
    takeLatest(AuthActions.GET_CURRENT_USER, getCurrentUser),
  ])
}
