import { delay } from "core/utils"
import axios from "services/_base/_axios"
import { IResponseCurrentUser } from "types/auth"

class AuthServices {
  private path = "auth"

  public async getCurrentUser() {
    const { data } = await axios.get<IResponseCurrentUser>(`/users/me`)

    return data
  }

  public async login(params: Record<"email" | "password", string>) {
    const { email, password } = params
    return axios.post(
      `/authentication`,
      {
        email,
        password,
      },
      { withCredentials: true }
    )
  }

  public async logout() {
    return axios.delete(`/authentication/me`)
  }
}

export default new AuthServices()
