import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
import { Grid, Box } from '@material-ui/core';
import { pxToRem } from 'resources/variables';
import IconButton from '@material-ui/core/IconButton';
import ClearIcon from '@material-ui/icons/Clear';
import LayersClearIcon from '@material-ui/icons/LayersClear';
import SendIcon from '@material-ui/icons/Send';

const styles = (theme: any) => {
  return {
    root: {
      position: 'absolute' as 'absolute',
      top: 50,
      right: 0,
    },
  };
};

const useStyles = makeStyles(styles);

function Index({
  showSimulationRoutes,
  handleClickClearDraw,
  handleClickClearDrawLayer,
  handleClickSend,
  ...props
}: any) {
  const classes = useStyles();

  return (
    <React.Fragment>
      {showSimulationRoutes && (
        <div className={classes.root}>
          <div onClick={handleClickClearDraw}>
            <IconButton color="secondary">
              <ClearIcon color="action" />
            </IconButton>
          </div>
          <div onClick={handleClickClearDrawLayer}>
            <IconButton color="secondary">
              <LayersClearIcon color="action" />
            </IconButton>
          </div>
          <div onClick={handleClickSend}>
            <IconButton color="secondary">
              <SendIcon color="action" />
            </IconButton>
          </div>
        </div>
      )}
    </React.Fragment>
  );
}

export const SimulationSettings = connect((state: any) => ({
  showSimulationRoutes: state.cargoMap.selectedCargo.simulationRoutes.show,
}))(Index);
