import React from 'react';
// import { makeStyles } from '@material-ui/core/styles';
import { connect } from 'react-redux';
// import IconButton from '@material-ui/core/IconButton';
// import ClearIcon from '@material-ui/icons/Clear';
// import LayersClearIcon from '@material-ui/icons/LayersClear';
// import SendIcon from '@material-ui/icons/Send';
import { ClearOutlined, LineChartOutlined, SendOutlined } from "@ant-design/icons"



const styles = (theme: any) => {
  return {
    root: {
      position: 'absolute' as 'absolute',
      top: 50,
      right: 0,
    },
  };
};


function Index({
  showSimulationRoutes,
  handleClickClearDraw,
  handleClickClearDrawLayer,
  handleClickSend,
  ...props
}: any) {

  return (
    <React.Fragment>
      {showSimulationRoutes && (
        <div className="simulationSetting">
          <div onClick={handleClickClearDraw}>
          <ClearOutlined/>
          </div>
          <div onClick={handleClickClearDrawLayer}>
            <LineChartOutlined/>
          </div>
          <div onClick={handleClickSend}>
            <SendOutlined/>
          </div>
        </div>
      )}
    </React.Fragment>
  );
}

export const SimulationSettings = connect((state: any) => ({
  showSimulationRoutes: state.cargoMap.selectedCargo.simulationRoutes.show,
}))(Index);
