import genActions from "services/_base/actions"
import {
  AuthActions,
  ILoginAction,
  ILoginFailedAction,
  ILoginSuccessAction,
  ILogoutAction,
  ILogoutFailedAction,
  ILogoutSuccessAction,
  IGetCurrentUserAction,
} from "./types"

export const { funcs: BaseFunctions, types: BaseActions } = genActions("auth")

export function login(payload: Record<"email" | "password", string>): ILoginAction {
  return { type: AuthActions.LOGIN, payload }
}

export function loginSuccess(): ILoginSuccessAction {
  return {
    type: AuthActions.LOGIN_SUCCESS,
  }
}

export function loginFailed(payload: { error: string }): ILoginFailedAction {
  return {
    type: AuthActions.LOGIN_FAILED,
    payload,
  }
}

export function logout(): ILogoutAction {
  return { type: AuthActions.LOGOUT }
}

export function logoutSuccess(): ILogoutSuccessAction {
  return {
    type: AuthActions.LOGOUT_SUCCESS,
  }
}

export function logoutFailed(payload: { error: string }): ILogoutFailedAction {
  return {
    type: AuthActions.LOGOUT_FAILED,
    payload,
  }
}

export function getCurrentUser(): IGetCurrentUserAction {
  return { type: AuthActions.GET_CURRENT_USER }
}
