import React, { Children } from "react"
import { Drawer } from "antd"

export interface DrawerProps {
  placement: "top" | "right" | "bottom" | "left"
  visible: boolean
  isCollapsed: boolean
  children: any
}

const Index = ({ placement, visible, isCollapsed, children }: DrawerProps) => {
  return (
    <>
      <Drawer
        title="Basic Drawer"
        placement={placement}
        closable={false}
        // onClose={this.onClose}
        visible={visible}
        key={placement}
        mask={false}
        zIndex={1}
        className={isCollapsed ? "" : "isCollapsed"}
      >
        {children}
      </Drawer>
    </>
  )
}

export default Index
