/* eslint-disable react-hooks/exhaustive-deps */
import { Select } from "antd"
import GuardianSelect from "components/GuardianSelect"
import GuardianSlider from "components/GuardianSlider"
import { WeatherElements } from "core/model/Weather"
import { keys, round } from "lodash"
import { Row, Col } from "antd"

const Index = () => {
  const { Option } = Select

  const onSelectWeather = (e) => {}
  const marks = () => {
    return {
      0: "01",
      24: "02",
      48: "03",
      72: "04",
      96: "05",
      120: "06",
      144: "07",
      168: "08",
      192: "09",
      216: "10",
      240: "11",
      264: "12",
      288: "13",
      312: "14",
    }
  }
  const tipFormatter = (value) => {
    const hour = value % 24
    return `${hour}:00`
  }
  return (
    <>
      <Row>
        <Col span={6} push={0}>
          <GuardianSelect
            placeholder="Weather Elements"
            onChange={onSelectWeather}
            className="WeatherSettingSelect"
          >
            {WeatherElements &&
              keys(WeatherElements).map((key: string) => (
                <Option value={key}>{WeatherElements[key]}</Option>
              ))}
          </GuardianSelect>
        </Col>
        <Col span={18} pull={2}>
          <GuardianSlider
            marks={marks()}
            step={1}
            defaultValue={144}
            max={312}
            className="WeatherSettingSlider"
            tooltipVisible
            tipFormatter={tipFormatter}
          />
        </Col>
      </Row>
    </>
  )
}

export default Index
