import { connect, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import React from 'react';
import { flowRight, compact } from 'lodash';
import { SimulationSettings } from 'components/SimulationSettings';
import { convertCoord2LongLat, convertHeadingtoDegree, convertLongLat2Coords } from 'core/model/Map';
import { formatDateTimeUTC, YYYY_MM_DD_HH_MM_SS } from 'core/utils/datetime';
import moment from 'moment';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import { simulationFormatMessage, aisFakeMessage } from 'core/model/Vessel';
import Map from "ol/Map"
import SET_STATE, { SIMULATE } from 'services/vessel/actions';


const vectorSource = new ol.source.Vector({
  format: new ol.format.GeoJSON(),
});

const getRotation = (startPoint: any, endPoint: any) => {
  const dx = endPoint[0] - startPoint[0];
  const dy = endPoint[1] - startPoint[1];
  return -Math.atan2(dy, dx);
};

const styleFunction = (feature: any) => {
  const geometry = feature.getGeometry();
  const styles = [
    // linestring
    new ol.style.Style({
      stroke: new ol.style.Stroke({
        color: '#ffcc33',
        width: 2,
      }),
    }),
  ];

  geometry.forEachSegment(function(start: any, end: any) {
    const rotation = getRotation(start, end);
    console.log('rotation' + rotation);
    // arrows
    styles.push(
      new ol.style.Style({
        geometry: new ol.geom.Point(end),
        image: new ol.style.Icon({
          src: 'resources/icons/arrow.png',
          anchor: [0.75, 0.5],
          rotateWithView: true,
          rotation: rotation,
        }),
      }),
    );
  });

  return styles;
};

interface simulationRoutesProps {
  guardianMap: Map
  selectedCargo: any
}

function Index({guardianMap , selectedCargo }: simulationRoutesProps) {
  const dispatch = useDispatch()
  const [currentFeature, setCurrentFeature] = useState(new ol.Feature());
  const [firstCoord, setFirstCoord] = useState(null);
  const {
    simulationRoutes: { update, show: showSimulationRoutes, data: SimulationRoutesData },
  } = selectedCargo;
  const createdAtTime = moment().toString();
  const [openDialog, setOpenDialog] = React.useState(false);

  const draw = new ol.interaction.Draw({
    source: vectorSource,
    type: 'LineString',
  });

  const simulationLayer = new ol.layer.Vector({
    source: vectorSource,
    style: styleFunction,
  });

  useEffect(() => {
    let increaseMinutes = 0;
    if (showSimulationRoutes && selectedCargo.id) {
      guardianMap.addLayer(simulationLayer);
      guardianMap.addInteraction(draw);
    }
    if (!showSimulationRoutes) {
      guardianMap.getInteractions().pop();
      simulationLayer.getSource().clear();
      guardianMap.removeLayer(simulationLayer);
    }
    draw.on('drawend', function(evt: any) {
      const feature = evt.feature;
      const coords = feature.getGeometry().getCoordinates();
      setFirstCoord(coords[0]);
      const simulationData = coords.map((coordinate: any, index: number) => {
        if (index !== 0) {
          const heading = convertHeadingtoDegree(getRotation(coords[index - 1], coordinate));
          const longLatCoord = convertCoord2LongLat(coordinate);
          const timeStamp = formatDateTimeUTC(
            moment(createdAtTime)
              .add(increaseMinutes, 'minutes')
              .toString(),
            YYYY_MM_DD_HH_MM_SS,
          );
          const message = simulationFormatMessage({ heading, longLatCoord, timeStamp });
          increaseMinutes += 5;

          return message;
        }
      });

      // dispatch({
      //   type: SET_STATE,
      //   payload: {
      //     selectedCargo: {
      //       simulationRoutes: {
      //         data: { $push: compact(simulationData) },
      //       },
      //     },
      //   },
      // });
      dispatch(SET_STATE())

      setCurrentFeature(feature);
    });
  }, [showSimulationRoutes, selectedCargo.id]);

  useEffect(() => {
    if (currentFeature && update) {
      const source = simulationLayer.getSource();
      const coords = SimulationRoutesData.map((record: any) => {
        return convertLongLat2Coords([record.longitude, record.latitude]);
      });
      const updateCoords = [firstCoord].concat(coords);
      const geometry = currentFeature.getGeometry();
      geometry.setCoordinates(updateCoords);
      source.clear();
      source.addFeature(currentFeature);

      const simulationData = geometry.getCoordinates().map((coordinate: any, index: number) => {
        if (index !== 0) {
          const heading = convertHeadingtoDegree(getRotation(updateCoords[index - 1], coordinate));
          const longLatCoord = convertCoord2LongLat(coordinate);
          const timeStamp = SimulationRoutesData[index - 1].timestamp;
          const speed = SimulationRoutesData[index - 1].speed;
          const created_at = SimulationRoutesData[index - 1].created_at;
          const message = simulationFormatMessage({ heading, longLatCoord, timeStamp, speed, created_at });

          return message;
        }
      });

      // dispatch({
      //   type: SET_STATE,
      //   payload: {
      //     selectedCargo: {
      //       simulationRoutes: {
      //         data: { $push: compact(simulationData) },
      //       },
      //     },
      //   },
      // });
      // dispatch({
      //   type: SET_STATE,
      //   payload: {
      //     selectedCargo: {
      //       simulationRoutes: {
      //         update: { $set: !update },
      //         data: { $set: compact(simulationData) },
      //       },
      //     },
      //   },
      // });
    }
  }, [update]);

  const handleClickClearDraw = () => {
    guardianMap.getInteractions().pop();
    guardianMap.addInteraction(draw);
  };

  const handleClickClearDrawLayer = () => {
    simulationLayer.getSource().clear();
    guardianMap.removeLayer(simulationLayer);
    guardianMap.addLayer(simulationLayer);
    // dispatch({
    //   type: SET_STATE,
    //   payload: {
    //     selectedCargo: {
    //       simulationRoutes: {
    //         data: { $set: [] },
    //       },
    //     },
    //   },
    // });
  };

  const handleClickSend = () => {
    simulationLayer.getSource().clear();
    guardianMap.removeLayer(simulationLayer);
    guardianMap.addLayer(simulationLayer);
    console.log(JSON.stringify(aisFakeMessage(SimulationRoutesData)));
    // dispatch({
    //   type: SET_STATE,
    //   payload: {
    //     selectedCargo: {
    //       simulationRoutes: {
    //         data: { $set: [] },
    //       },
    //     },
    //   },
    // });
    // dispatch({
    //   type: SIMULATE,
    //   payload: {
    //     imo: "123456",
    //     SimulationRoutesData,
    //   },
    // });
    setOpenDialog(false);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };
  const handleOpenDialog = () => {
    setOpenDialog(true);
  };

  return (
    <>
      <SimulationSettings
        handleClickClearDraw={handleClickClearDraw}
        handleClickClearDrawLayer={handleClickClearDrawLayer}
        handleClickSend={handleOpenDialog}
      />
      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{'Create Simulation Data Confirmation?'}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Please be aware that this simulation data will effect to voyage of current vessel, it is recommended to use
            on a fake vessel which is not belong to our customer, will you continue?
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClickSend} color="primary" autoFocus>
            Send
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
}

export default Index
